var $ = require('jquery');

function CBObject(position,type){
	this.position = typeof position !== 'undefined' ? position : null;
	this.type = typeof type !== 'undefined' ? type : "CBObject";
}

CBObject.prototype.editorView = function editorView() {
	var aux = $(window.document.createElement('div'));
	aux.css('left', this.position[0]);
	aux.css('top', this.position[1]);
	aux.addClass('draggable');
	return aux;
};

CBObject.prototype.save = function save() {
	return {'type':this.type,'position':this.position};
}
module.exports = CBObject;